/* eslint-disable vue/multi-word-component-names */
<template>
  <div class="single-item container-fluid">
    <VueSlickCarousel
      v-bind="settings"
      :slidesToShow="numOfSlides.num"
      v-if="getOfInfoItems.length"
    >
      <div
        class="item-list"
        v-for="infoItem in getOfInfoItems"
        :key="infoItem.id"
      >
        <router-link :to="'/Single/' + infoItem.id">
          <div class="image-box">
            <img ref="image" :src="infoItem.image" alt="index" />
          </div>
        </router-link>
        <div class="ShowMe">
          <button
            :class="[infoItem.like ? 'green' : 'gray']"
            @click="toggle(infoItem.id)"
          >
            Like
          </button>
        </div>
      </div>
    </VueSlickCarousel>
    {{ numOfSlide }}
  </div>
</template>
<script>
import VueSlickCarousel from "vue-slick-carousel";
import "vue-slick-carousel/dist/vue-slick-carousel.css";
// optional style for arrows & dots
import "vue-slick-carousel/dist/vue-slick-carousel-theme.css";

export default {
  name: "single-item",
  props: ["numOfSlide"],
  components: { VueSlickCarousel },
  data: () => ({
    settings: {
      dots: false,
      arrows: false,
      focusOnSelect: true,
      infinite: true,
      autoplay: true,
      speed: 500,
      slidesToScroll: 1,
      touchThreshold: 2,
    },
  }),
  mounted() {
    this.$refs.image.style = "";
  },
  computed: {
    infoItems() {
      return this.$store.state.localInfo;
    },
    getOfInfoItems() {
      return this.$store.getters.getOfInfoItems;
    },
    getImageFromItem() {
      return require(this.getOfInfoItems().image);
    },
    numOfSlides() {
      return this.numOfSlide;
    },
  },
  methods: {
    toggle: function (id) {
      this.$store.dispatch("toggle", id);
    },
  },
};
</script>

<style scoped lang="scss">
.single-item {
  // display: flex;
  // justify-content: space-between;
  // overflow: hidden;
  // overflow-x: auto;
  .item-list {
    .image-box {
      height: 300px;
      margin: 10px;
      img {
        display: block;
        width: 100%;
        height: 100%;
      }
    }
    .ShowMe {
      display: flex;
      justify-content: space-between;
      width: 100%;
      button {
        border: none;
        background: dimgrey;
        font-size: 14px;
        margin: 0 5px;
        width: 100%;
      }
      .green {
        background: green;
        color: white;
      }
      .gray {
        background: dimgrey;
        color: white;
      }
    }
  }
}
::-webkit-scrollbar {
  width: 0; /* Remove scrollbar space */
  background: transparent; /* Optional: just make scrollbar invisible */
}
/* Optional: show position indicator in red */
::-webkit-scrollbar-thumb {
  background: transparent;
}
.image-width-full {
  width: 100%;
}
.image-width {
  width: 100%;
}
</style>
