import axios from "axios";
export const namespaced = true;
// JSON.parse(localStorage.getItem("token")
// export const plugins = [saveStorePlugins];
export const state = {
  users: {
    jwt: "",
    username: "",
  },
};
export const getters = {
  getUsers: (state) => {
    return state.users;
  },
};
export const mutations = {
  SET_TOKEN: (state, token) => {
    state.users.jwt = token;
  },
  SET_USER: (state, username) => {
    state.users.username = username;
  },
};
export const actions = {
  async signIn({ dispatch }, credentials) {
    const response = await axios.post(
      "http://localhost:1337/api/auth/local",
      credentials
    );
    if (response.status == 429) {
      this.activeToMany = true;
      this.errors.push("Many Requests, wait for a One Minute");
      setTimeout(() => {
        this.activeToMany = false;
        this.errors = [];
      }, 60000);
    }
    if (response.ok) {
      this.errors = [];
      return response.json();
    } else {
      this.errors.push("Username & password are wrong");
    }
    // console.log(response.data.user.username);
    dispatch("attempt", response.data.jwt);
    return ;
  },

  async attempt({ commit }, token) {
    commit("SET_TOKEN", token);
    // commit("SET_USER", user.user.username);
  },
};
