/* eslint-disable vue/multi-word-component-names */
<template>
  <div class="single-item">
    <VueSlickCarousel :arrows="true" :dots="true">
      <div>1</div>
      <div>2</div>
      <div>3</div>
      <div>4</div>
    </VueSlickCarousel>
    <div
      class="item-list"
      v-for="infoItem in getOfInfoItems"
      :key="infoItem.id"
    >
      <router-link :to="'/Single/' + infoItem.id">
        <div class="image-box">
          <img :src="infoItem.image" alt="index" />
        </div>
      </router-link>
      <div class="ShowMe">
        <button
          :class="[infoItem.like ? 'green' : 'gray']"
          @click="toggle(infoItem.id)"
        >
          Like
        </button>
      </div>
    </div>
  </div>
</template>
<script>
export default {
  name: "single-item",
  computed: {
    infoItems() {
      return this.$store.state.localInfo;
    },
    getOfInfoItems() {
      return this.$store.getters.getOfInfoItems;
    },
    getImageFromItem() {
      return require(this.getOfInfoItems().image);
    },
  },
  methods: {
    toggle: function (id) {
      this.$store.dispatch("toggle", id);
    },
  },
};
</script>

<style scoped lang="scss">
.single-item {
  display: flex;
  justify-content: space-between;
  overflow: hidden;
  overflow-x: auto;
  .item-list {
    .image-box {
      width: 200px;
      height: 300px;
      margin: 10px;
      img {
        display: block;
        width: 100%;
        height: 100%;
      }
    }
    .ShowMe {
      display: flex;
      justify-content: space-between;
      width: 100%;
      button {
        border: none;
        background: dimgrey;
        font-size: 14px;
        margin: 0 5px;
        width: 100%;
      }
      .green {
        background: green;
        color: white;
      }
      .gray {
        background: dimgrey;
        color: white;
      }
    }
  }
}
::-webkit-scrollbar {
